list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?subclass ?authorship ?infoResource ?infoResourceName ?dateTime ?journal ?volume ?startPage ?endPage ?publisher ?locale ?appearsIn ?partOf ?editor ?hideThis WHERE { ?subject ?property ?authorship . ?authorship core:relates ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource rdfs:label ?infoResourceName .
OPTIONAL { ?infoResource bibo:volume ?volume } OPTIONAL { ?infoResource bibo:pageStart ?startPage } OPTIONAL { ?infoResource bibo:pageEnd ?endPage } OPTIONAL { ?infoResource core:placeOfPublication ?locale } OPTIONAL { ?infoResource bibo:reproducedIn ?appearsInObj . ?appearsInObj rdfs:label ?appearsIn } OPTIONAL { ?infoResource core:publisher ?publisherObj . ?publisherObj rdfs:label ?publisher } OPTIONAL { ?infoResource core:relatedBy ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editorObj . ?editorObj rdfs:label ?editor } OPTIONAL { ?infoResource <http://purl.obolibrary.org/obo/BFO_0000050> ?partOfObj . ?partOfObj rdfs:label ?partOf } OPTIONAL { ?infoResource vitro:mostSpecificType ?subclass } OPTIONAL { ?infoResource core:hasPublicationVenue ?publishedIn . ?publishedIn rdfs:label ?journal } OPTIONAL { ?infoResource core:dateTimeValue ?dateTimeValue . ?dateTimeValue core:dateTime ?dateTime } OPTIONAL { ?authorship core:hideFromDisplay ?hideThis }
FILTER ( bound(?infoResource) )
} ORDER BY ?subclass DESC(?dateTime) ?infoResourceName

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX bibo: <http://purl.org/ontology/bibo/>
CONSTRUCT {
    ?subject ?property ?authorship .
    ?authorship a core:Authorship .
    ?authorship core:relates ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource rdfs:label ?infoResourceName .
    ?infoResource core:hasPublicationVenue ?publishedIn .
    ?publishedIn  rdfs:label ?journal
} WHERE {
    {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship
    }
    UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030>
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource rdfs:label ?infoResourceName
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource core:hasPublicationVenue ?publishedIn
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource core:hasPublicationVenue ?publishedIn .
       ?publishedIn  rdfs:label ?journal
    }
}
