list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?awardedDegree ?degree ?degreeName ?degreeAbbr WHERE { ?subject ?property ?awardedDegree . ?awardedDegree core:relates ?degree . ?degree rdfs:label ?degreeName
OPTIONAL { ?degree core:abbreviation ?degreeAbbr } }

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>   
PREFIX core: <http://vivoweb.org/ontology/core#>

CONSTRUCT {
        ?subject ?property ?awardedDegree .
        ?awardedDegree core:relates ?degree .
        ?degree rdfs:label ?degreeName .
        ?degree core:abbreviation ?degreeAbbr 
} WHERE {
    { 
        ?subject ?property ?awardedDegree .
        ?awardedDegree a core:AwardedDegree .
        ?awardedDegree core:relates ?degree .
        ?degree a core:AcademicDegree .
        ?degree rdfs:label ?degreeName 
    } UNION {
        ?subject ?property ?awardedDegree .
        ?awardedDegree a core:AwardedDegree .
        ?awardedDegree core:relates ?degree .
        ?degree a core:AcademicDegree .
        ?degree core:abbreviation ?degreeAbbr 
    }
}
