list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?activity ?activityName ?activityLabel ?awardedBy ?adminedBy ?awardedByLabel ?adminedByLabel ?dateTimeStartRole ?dateTimeEndRole ?dateTimeStartGrant ?dateTimeEndGrant WHERE {
?subject ?property ?role
OPTIONAL { ?role core:roleRealizedIn ?activity LET (?activityName := afn:localname(?activity)) OPTIONAL { ?activity rdfs:label ?activityLabel }
?activity vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:hasInvestigatorRole
} OPTIONAL { ?role core:roleContributesTo ?activity LET (?activityName := afn:localname(?activity)) OPTIONAL { ?activity rdfs:label ?activityLabel }
?activity vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:hasInvestigatorRole
} OPTIONAL { ?activity core:grantAwardedBy ?awardedBy . ?awardedBy rdfs:label ?awardedByLabel } OPTIONAL { ?activity core:administeredBy ?adminedBy . ?adminedBy rdfs:label ?adminedByLabel }
OPTIONAL { ?role rdfs:label ?roleLabel } OPTIONAL { ?role core:dateTimeInterval ?dateTimeIntervalRole OPTIONAL { ?dateTimeIntervalRole core:start ?dateTimeStartValueRole . ?dateTimeStartValueRole core:dateTime ?dateTimeStartRole } OPTIONAL { ?dateTimeIntervalRole core:end ?dateTimeEndValueRole . ?dateTimeEndValueRole core:dateTime ?dateTimeEndRole } } OPTIONAL { ?role rdfs:label ?roleLabel } OPTIONAL { ?role core:roleContributesTo ?grant . ?grant core:dateTimeInterval ?dateTimeIntervalGrant OPTIONAL { ?dateTimeIntervalGrant core:start ?dateTimeStartValueGrant . ?dateTimeStartValueGrant core:dateTime ?dateTimeStartGrant } OPTIONAL { ?dateTimeIntervalGrant core:end ?dateTimeEndValueGrant . ?dateTimeEndValueGrant core:dateTime ?dateTimeEndGrant } }
FILTER ( bound(?activity) )
} ORDER BY ?subclass DESC(?dateTimeEnd) DESC(?dateTimeStart) ?activityLabel ?activityName

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue . 
    ?activity rdfs:label ?activityName .
    ?activity core:grantAwardedBy ?awardedBy .
    ?awardedBy rdfs:label ?awardedByLabel .
    ?activity core:administeredBy ?adminedBy .
    ?adminedBy rdfs:label ?adminedByLabel
} WHERE {
    {
        ?subject ?property ?role 
    } UNION {
        ?subject ?property ?role .
        ?role ?roleProperty ?roleValue 
    } UNION {
        ?subject ?property ?role .
        ?role core:roleContributesTo ?activity . 
        ?activity rdfs:label ?activityName 
    } UNION {
        ?subject ?property ?role .
        ?role core:roleRealizedIn ?activity . 
        ?activity rdfs:label ?activityName                
    } UNION {
        ?subject ?property ?role .
        ?role core:roleContributesTo ?activity . 
        ?activity core:grantAwardedBy ?awardedBy
    } UNION {
        ?subject ?property ?role .
        ?role core:roleContributesTo ?activity . 
        ?activity core:grantAwardedBy ?awardedBy .
        ?awardedBy rdfs:label ?awardedByLabel
    } UNION {
        ?subject ?property ?role .
        ?role core:roleContributesTo ?activity . 
        ?activity core:administeredBy ?adminedBy
    } UNION {
        ?subject ?property ?role .
        ?role core:roleContributesTo ?activity . 
        ?activity core:administeredBy ?adminedBy .
        ?adminedBy rdfs:label ?adminedByLabel
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeIntervalRole .
    ?dateTimeIntervalRole core:start ?dateTimeStartValueRole .
    ?dateTimeStartValueRole core:dateTime ?dateTimeStartRole 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeIntervalRole .
    ?dateTimeIntervalRole core:start ?dateTimeStartValueRole .
    ?dateTimeStartValueRole core:dateTime ?dateTimeStartRole 
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeIntervalRole .
    ?dateTimeIntervalRole core:end ?dateTimeEndValueRole .
    ?dateTimeEndValueRole core:dateTime ?dateTimeEndRole 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeIntervalRole .
    ?dateTimeIntervalRole core:end ?dateTimeEndValueRole .
    ?dateTimeEndValueRole core:dateTime ?dateTimeEndRole 
}
