list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?subclassLabel ?activity (afn:localname(?activity) AS ?activityLocal) ?activityName ?dateTimeStart ?dateTimeEnd WHERE {
?subject ?property ?role OPTIONAL { ?role core:roleContributesTo ?activity . ?activity rdfs:label ?activityName . }
OPTIONAL { ?role core:roleContributesTo ?activity . ?activity vitro:mostSpecificType ?subclass . ?subclass rdfs:label ?subclassLabel } OPTIONAL { ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd }
} ORDER BY ?subclass DESC(?dateTimeEnd) DESC(?dateTimeStart) ?activityName

list-view-config.query-construct:
    PREFIX core: <http://vivoweb.org/ontology/core#>
    PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
    PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

    CONSTRUCT {
        ?subject ?property ?role .
        ?role a core:ReviewerRole .
        ?role vitro:mostSpecificType ?roleSubclass .
        ?role core:roleContributesTo ?activity .
        ?activity rdfs:label ?activityName .
        ?activity vitro:mostSpecificType ?subclass .
        ?subclass rdfs:label ?subclassLabel
    } WHERE {
    {
        ?subject ?property ?role .
        ?role a core:ReviewerRole .
    } UNION {
        ?subject ?property ?role .
        ?role a core:ReviewerRole .
        ?role vitro:mostSpecificType ?roleSubclass
    } UNION {
        ?subject ?property ?role .
        ?role a core:ReviewerRole .
        ?role core:roleContributesTo ?activity .
        ?activity rdfs:label ?activityName .
    } UNION {
        ?subject ?property ?role .
        ?role a core:ReviewerRole .
        ?role core:roleContributesTo ?activity .
        ?activity vitro:mostSpecificType ?subclass .
        ?subclass rdfs:label ?subclassLabel
    }
}
