list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#>
SELECT DISTINCT ?subclass ?position ?positionTitle ?person ?personName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?position OPTIONAL { ?position core:positionForPerson ?person . ?person rdfs:label ?personName }
OPTIONAL { ?position a ?subclass . ?subclass rdfs:subClassOf core:Position }
OPTIONAL { ?position rdfs:label ?positionTitle } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd } # Get current positions only: end date is either null or not in the past } FILTER ( !bound(?dateTimeEnd) || afn:substring(str(?dateTimeEnd), 0, 4) >= afn:substring(str(afn:now()), 0, 4) )
FILTER ( bound(?person) )
} ORDER BY ?subclass ?personName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>  
CONSTRUCT {
    ?subclass rdfs:subClassOf core:Position 
} WHERE {
    ?subclass rdfs:subClassOf core:Position 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?position .
    ?position ?positionProperty ?positionValue .
    ?person rdfs:label ?personName  
} WHERE {
    {
       ?subject ?property ?position 
    } UNION {
       ?subject ?property ?position .
       ?position ?positionProperty ?positionValue 
    } UNION {
       ?subject ?property ?position .
       ?position core:positionForPerson ?person .
       ?person rdfs:label ?personName   
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
}

PREFIX core: <http://vivoweb.org/ontology/core#>    
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
} WHERE {
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
}
