list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?position ?positionTitle ?hrJobTitle ?org ?orgName ?middleOrg ?middleOrgName ?outerOrg ?outerOrgName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?position OPTIONAL { ?position core:positionInOrganization ?org . ?org rdfs:label ?orgName OPTIONAL { ?org core:subOrganizationWithin ?middleOrg . ?middleOrg rdfs:label ?middleOrgName OPTIONAL { ?middleOrg core:subOrganizationWithin ?outerOrg . ?outerOrg rdfs:label ?outerOrgName } } } OPTIONAL { ?position rdfs:label ?positionTitle } OPTIONAL { ?position core:hrJobTitle ?hrJobTitle } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd } }
OPTIONAL { ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:Position }
FILTER ( bound(?org) )
} ORDER BY ?subclass DESC(?dateTimeEnd) DESC(?dateTimeStart) ?orgName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>  
CONSTRUCT {
    ?subject ?property ?position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf core:Position 
} WHERE {
    ?subject ?property ?position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf core:Position 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?position .
    ?position ?positionProperty ?positionValue .
    ?org rdfs:label ?orgName .
    ?org core:subOrganizationWithin ?middleOrg .
    ?middleOrg rdfs:label ?middleOrgName .
    ?middleOrg core:subOrganizationWithin ?outerOrg .
    ?outerOrg rdfs:label ?outerOrgName
} WHERE {
    {
       ?subject ?property ?position 
    } UNION {
       ?subject ?property ?position .
       ?position ?positionProperty ?positionValue 
    } UNION {
       ?subject ?property ?position .
       ?position core:positionInOrganization ?org . 
       ?org rdfs:label ?orgName 
    } UNION {
        ?subject ?property ?position .
        ?position core:positionInOrganization ?org . 
        ?org core:subOrganizationWithin ?middleOrg
    } UNION {
        ?subject ?property ?position .
        ?position core:positionInOrganization ?org . 
        ?org core:subOrganizationWithin ?middleOrg .
        ?middleOrg rdfs:label ?middleOrgName
    } UNION {
        ?subject ?property ?position .
        ?position core:positionInOrganization ?org . 
        ?org core:subOrganizationWithin ?middleOrg .
        ?middleOrg core:subOrganizationWithin ?outerOrg
    } UNION {
        ?subject ?property ?position .
        ?position core:positionInOrganization ?org . 
        ?org core:subOrganizationWithin ?middleOrg .
        ?middleOrg core:subOrganizationWithin ?outerOrg .
        ?outerOrg rdfs:label ?outerOrgName
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
}

PREFIX core: <http://vivoweb.org/ontology/core#>    
CONSTRUCT { 
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
} WHERE {
    ?subject ?property ?position .  
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
}
