list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT DISTINCT ?subclass ?position ?positionTitle ?hrJobTitle ?rank ?org ?orgName ?middleOrg ?middleOrgName ?outerOrg ?outerOrgName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?position . OPTIONAL { ?position core:relates ?org . ?org rdfs:label ?orgName } OPTIONAL { ?position core:relates ?org . ?org rdfs:label ?orgName . ?org obo:BFO_0000050 ?middleOrg . ?middleOrg rdfs:label ?middleOrgName } OPTIONAL { ?position core:relates ?org . ?org rdfs:label ?orgName . ?org obo:BFO_0000050 ?middleOrg . ?middleOrg obo:BFO_0000050 ?outerOrg . ?outerOrg rdfs:label ?outerOrgName } OPTIONAL { ?position rdfs:label ?positionTitle } OPTIONAL { ?position core:hrJobTitle ?hrJobTitle } OPTIONAL { ?position core:rank ?rank } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd }
OPTIONAL { ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:Position }
FILTER ( bound(?org) )
} ORDER BY ?subclass DESC(?dateTimeEnd) ?rank

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
CONSTRUCT {
    ?subject ?property ?position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf core:Position
} WHERE {
    ?subject ?property ?position .
    ?position a core:Position .
    ?position vitro:mostSpecificType ?subclass .
    ?subclass rdfs:subClassOf core:Position
}

        PREFIX core: <http://vivoweb.org/ontology/core#>
        PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
        PREFIX foaf: <http://xmlns.com/foaf/0.1/>
        PREFIX obo:   <http://purl.obolibrary.org/obo/>
        CONSTRUCT {
        	?subject ?property ?position .
			?position rdfs:label ?positionTitle .
			?position core:hrJobTitle ?hrJobTitle .
			?position core:rank ?rank .
			?position core:relates ?org .
        	?org rdfs:label ?orgName .
        	?org obo:BFO_0000050 ?middleOrg .
        	?middleOrg rdfs:label ?middleOrgName .
        	?middleOrg obo:BFO_0000050 ?outerOrg .
        	?outerOrg rdfs:label ?outerOrgName .
        } WHERE {
            {
               ?subject ?property ?position .
               ?position a core:Position .
            } UNION {
               ?subject ?property ?position .
               ?position a core:Position .
               ?position rdfs:label ?positionTitle .
            } UNION {
               ?subject ?property ?position .
               ?position a core:Position .
               ?position core:rank ?rank
            } UNION {
               ?subject ?property ?position .
               ?position a core:Position .
               ?position core:relates ?org .
               ?org a foaf:Organization .
               ?org rdfs:label ?orgName
            } UNION {
                ?subject ?property ?position .
                ?position a core:Position .
                ?position core:relates ?org .
                ?org a foaf:Organization .
                ?org obo:BFO_0000050 ?middleOrg .
                ?middleOrg rdfs:label ?middleOrgName
            } UNION {
                ?subject ?property ?position .
                ?position a core:Position .
                ?position core:relates ?org .
                ?org a foaf:Organization .
                ?org obo:BFO_0000050 ?middleOrg .
                ?middleOrg obo:BFO_0000050 ?outerOrg .
                ?outerOrg rdfs:label ?outerOrgName
            }
        }

PREFIX core: <http://vivoweb.org/ontology/core#>
CONSTRUCT {
    ?subject ?property ?position .
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart .
} WHERE {
    ?subject ?property ?position .
    ?position a core:Position .
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart .
}

PREFIX core: <http://vivoweb.org/ontology/core#>
CONSTRUCT {
    ?subject ?property ?position .
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd
} WHERE {
    ?subject ?property ?position .
    ?position a core:Position .
    ?position core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd
}
