list-view-config.query-select:
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT DISTINCT ?vcard ?title ?preferredTitle WHERE { ?subject ?property ?vcard . ?vcard vcard:hasTitle ?title . ?title vcard:title ?preferredTitle
}

list-view-config.query-construct:
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
CONSTRUCT {
    ?subject ?property ?vcard .
    ?vcard vcard:hasTitle ?title .
    ?title ?titleProperty ?titleValue
} WHERE {
    {
       ?subject ?property ?vcard
    } UNION {
       ?subject ?property ?vcard .
       ?vcard vcard:hasTitle ?title
    } UNION {
       ?subject ?property ?vcard .
       ?vcard vcard:hasTitle ?title .
       ?title ?titleProperty ?titleValue
    }
}
