list-view-config.query-select:
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT DISTINCT ?vcard ?email ?emailAddress WHERE { ?subject ?property ?vcard . ?vcard vcard:hasEmail ?email . ?email a vcard:Work . ?email vcard:email ?emailAddress
}

list-view-config.query-construct:
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
CONSTRUCT { 
    ?subject ?property ?vcard .
    ?vcard vcard:hasEmail ?email .
    ?email a vcard:Work .
    ?email ?emailProperty ?emailValue 
} WHERE {
       ?subject ?property ?vcard .
       ?vcard vcard:hasEmail ?email .
       ?email a vcard:Work .
       ?email ?emailProperty ?emailValue 
}
