list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX bibo: <http://purl.org/ontology/bibo/>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

SELECT DISTINCT ?subclass
    ?infoResource ?infoResourceName
    ?dateTime
    ?volume
    ?startPage
    ?endPage
    ?locale
WHERE
{
    ?subject ?property ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource rdfs:label ?infoResourceName .

    OPTIONAL { ?infoResource bibo:volume ?volume }
    OPTIONAL { ?infoResource bibo:pageStart ?startPage }
    OPTIONAL { ?infoResource bibo:pageEnd ?endPage }
    OPTIONAL { ?infoResource core:placeOfPublication ?locale }
    OPTIONAL { ?infoResource vitro:mostSpecificType ?subclass }
    OPTIONAL
    {
        ?infoResource core:dateTimeValue ?dateTimeValue .
        ?dateTimeValue core:dateTime ?dateTime
    }


    FILTER ( bound(?infoResource) )

} ORDER BY ?subclass  DESC(?dateTime) ?infoResourceName

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
CONSTRUCT { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource vitro:mostSpecificType ?subclass . ?infoResource rdfs:label ?infoResourceName . ?infoResource bibo:volume ?volume . ?infoResource bibo:pageStart ?startPage . ?infoResource bibo:pageEnd ?endPage . ?infoResource core:placeOfPublication ?locale . ?infoResource core:dateTimeValue ?dateTimeValue . ?dateTimeValue core:dateTime ?dateTime .
?infoResource core:relatedBy ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editorObj . ?editorObj a foaf:Person . ?editorObj rdfs:label ?editor . } WHERE { { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource vitro:mostSpecificType ?subclass . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource rdfs:label ?infoResourceName . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource bibo:volume ?volume . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource bibo:pageStart ?startPage . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource bibo:pageEnd ?endPage . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource core:placeOfPublication ?locale . } UNION { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource rdfs:label ?infoResourceName . ?infoResource core:dateTimeValue ?dateTimeValue . ?dateTimeValue core:dateTime ?dateTime . } UNION { ?subject ?property ?infoResource . ?infoResource a bibo:Book . ?infoResource core:relatedBy ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editorObj . ?editorObj a foaf:Person . ?editorObj rdfs:label ?editor } UNION { ?subject ?property ?infoResource . ?infoResource a bibo:BookSection . ?infoResource core:relatedBy ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editorObj . ?editorObj a foaf:Person . ?editorObj rdfs:label ?editor } }
