list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?subclass ?infoResource ?infoResourceName ?dateTime ?volume ?startPage ?endPage ?locale WHERE { ?subject ?property ?infoResource . ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> . ?infoResource rdfs:label ?infoResourceName .
OPTIONAL { ?infoResource bibo:volume ?volume } OPTIONAL { ?infoResource bibo:pageStart ?startPage } OPTIONAL { ?infoResource bibo:pageEnd ?endPage } OPTIONAL { ?infoResource core:placeOfPublication ?locale } OPTIONAL { ?infoResource vitro:mostSpecificType ?subclass } OPTIONAL { ?infoResource core:dateTimeValue ?dateTimeValue . ?dateTimeValue core:dateTime ?dateTime }
FILTER ( bound(?infoResource) )
} ORDER BY ?subclass DESC(?dateTime) ?infoResourceName

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX bibo: <http://purl.org/ontology/bibo/>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>    
CONSTRUCT {
    ?subject ?property ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource vitro:mostSpecificType ?subclass 
} WHERE {
    ?subject ?property ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource vitro:mostSpecificType ?subclass 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
PREFIX bibo: <http://purl.org/ontology/bibo/> 
CONSTRUCT { 
    ?subject ?property ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource rdfs:label ?infoResourceName .
} WHERE {
    {
       ?subject ?property ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030>  
    } UNION {
       ?subject ?property ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource rdfs:label ?infoResourceName 
    }        }
