list-view-config.query-select:

PREFIX afn:   <http://jena.apache.org/ARQ/function#>
PREFIX bibo:  <http://purl.org/ontology/bibo/>
PREFIX vivo:  <http://vivoweb.org/ontology/core#>
PREFIX owl:   <http://www.w3.org/2002/07/owl#>
PREFIX rdfs:  <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX event: <http://purl.org/NET/c4dm/event.owl#>

SELECT DISTINCT ?subclass
                ?role ?roleLabel                         
                ?event1 ?event1Name ?event1Label
                ?event2 ?event2Label
                ?event3 ?event3Label
                ?series ?seriesLabel
                ?dateTimeStart
                ?dateTimeEnd

    WHERE {

        ?subject ?property ?role .

        OPTIONAL { ?role rdfs:label ?roleLabel . }

        OPTIONAL {  ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 .
                    LET (?event1Name := afn:localname(?event1)) 

                    OPTIONAL { ?event1 rdfs:label ?event1Label . }

                    OPTIONAL { ?event2 a event:Event .
                               OPTIONAL { ?event2 rdfs:label ?event2Label . }

                               { 
                                   ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?event2 .
                               }  UNION {                                                                                                              
                                   ?event2 <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 .
                               }
                    }
                    OPTIONAL {
                               { 
                                    ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?event3 .
                                    ?event3 <http://purl.obolibrary.org/obo/BFO_0000050> ?event2 .
                               }  UNION {   
                                    ?event2 <http://purl.obolibrary.org/obo/BFO_0000051> ?event3 .                                                                                                          
                                    ?event3 <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 .

                               }
                               OPTIONAL { ?event3 rdfs:label ?event3Label . }
                    }


                    OPTIONAL { ?series a vivo:EventSeries .
                               OPTIONAL { ?series rdfs:label ?seriesLabel . }

                               { 
                                   ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?series .
                               }  UNION {                                                                                                              
                                   ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 .
                               }
                    }
                    OPTIONAL {
                               { 
                                   ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?event3 .
                                   ?event3 <http://purl.obolibrary.org/obo/BFO_0000050> ?series .
                               }  UNION {                                                                                                              
                                   ?event3 <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 .
                                   ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?event3 .
                               }
                               OPTIONAL { ?series rdfs:label ?seriesLabel . }
                    }


                    ?event1 vitro:mostSpecificType ?subclass .

                    { 
                        ?subclass rdfs:subClassOf event:Event .
                    } UNION {
                        ?subclass  owl:equivalentClass event:Event .
                    }


        }   

        OPTIONAL { ?role vivo:dateTimeInterval ?dateTimeInterval .
                   OPTIONAL { ?dateTimeInterval vivo:start ?dateTimeStartValue .
                              ?dateTimeStartValue vivo:dateTime ?dateTimeStart .
                   }
                   OPTIONAL { ?dateTimeInterval vivo:end ?dateTimeEndValue .
                              ?dateTimeEndValue vivo:dateTime ?dateTimeEnd .
                   }
        }  


        FILTER ( bound(?event1) )


    } ORDER BY ?subclass DESC(?dateTime) ?event1Label ?event1Name

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role a vivo:AttendeeRole .
    ?event1 rdfs:label ?event1Label 
} WHERE {
    {
        ?subject ?property ?role .
        ?role a vivo:AttendeeRole .
    } UNION {
        ?subject ?property ?role .
        ?role a vivo:AttendeeRole .
        ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 . 
        ?event1 rdfs:label ?event1Label 
    }
}

PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#>
CONSTRUCT { ?subject ?property ?role . ?role a vivo:AttendeeRole . ?event2 a event:Event . ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?event2 . ?event2 <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 . ?event2 rdfs:label ?event2Label } WHERE {
{ ?subject ?property ?role . ?role a vivo:AttendeeRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 . ?event2 a event:Event . ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?event2 . ?event2 rdfs:label ?event2Label } UNION { ?subject ?property ?role . ?role a vivo:AttendeeRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 . ?event2 a event:Event . ?event2 <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 . ?event2 rdfs:label ?event2Label } }

PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#>
CONSTRUCT { ?subject ?property ?role . ?role a vivo:AttendeeRole . ?series a vivo:EventSeries . ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 . ?series rdfs:label ?seriesLabel } WHERE {
{ ?subject ?property ?role . ?role a vivo:AttendeeRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 . ?series a vivo:EventSeries . ?event1 <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series rdfs:label ?seriesLabel } UNION { ?subject ?property ?role . ?role a vivo:AttendeeRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?event1 . ?series a vivo:EventSeries . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?event1 . ?series rdfs:label ?seriesLabel } }
