list-view-config.query-select:
PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?roleLabel ?presentation ?presentationName ?presentationLabel ?conference ?conferenceLabel ?series ?seriesLabel ?workshop ?dateTime
WHERE { ?subject ?property ?role . ?role a vivo:PresenterRole .
OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role rdfs:label ?roleLabel . } OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation rdfs:label ?presentationLabel . LET (?presentationName := REPLACE(STR(?presentation),"^.*(#)(.*)$", "$2")) } OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?conference a bibo:Conference . ?conference rdfs:label ?conferenceLabel . } OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?workshop . ?workshop <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?workshop <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?workshop . ?conference rdfs:label ?conferenceLabel . } OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?series rdfs:label ?seriesLabel . } OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?workshop . ?workshop <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?workshop <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?workshop . ?series rdfs:label ?seriesLabel . }
OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation vitro:mostSpecificType ?subclass . }
OPTIONAL { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role vivo:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval vivo:start ?dateTimeStartValue . ?dateTimeStartValue vivo:dateTime ?dateTime . }
FILTER ( bound(?presentation) )
} ORDER BY ?subclass DESC(?dateTime) ?presentationLabel ?presentationName
