list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?roleLabel ?presentation ?presentationName ?presentationLabel ?conference ?conferenceLabel ?series ?seriesLabel ?workshop ?dateTime
WHERE {
?subject ?property ?role . ?role a vivo:PresenterRole
OPTIONAL { ?role rdfs:label ?roleLabel }
OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation rdfs:label ?presentationLabel . LET (?presentationName := afn:localname(?presentation)) } OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?conference a bibo:Conference . ?conference rdfs:label ?conferenceLabel } OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?workshop . ?workshop <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?workshop <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?workshop . ?conference rdfs:label ?conferenceLabel } OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?series rdfs:label ?seriesLabel } OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?workshop . ?workshop <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?workshop <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?workshop . ?series rdfs:label ?seriesLabel }
OPTIONAL { ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation vitro:mostSpecificType ?subclass . }
OPTIONAL { ?role vivo:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval vivo:start ?dateTimeStartValue . ?dateTimeStartValue vivo:dateTime ?dateTime }
FILTER ( bound(?presentation) )
} ORDER BY ?subclass DESC(?dateTime) ?presentationLabel ?presentationName

list-view-config.query-construct:
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>   
CONSTRUCT { 
    ?subject ?property ?role .
    ?role a vivo:PresenterRole .
    ?role rdfs:label ?roleLabel .
    ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation .
    ?presentation a vivo:Presentation .
    ?presentation rdfs:label ?presentationLabel .
    ?presentation vitro:mostSpecificType ?subclass .
} WHERE {
    {
        ?subject ?property ?role .
        ?role a vivo:PresenterRole 
    } UNION {
        ?subject ?property ?role .
        ?role a vivo:PresenterRole .
        ?role rdfs:label ?roleLabel .
    } UNION {
        ?subject ?property ?role .
        ?role a vivo:PresenterRole .
        ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . 
        ?presentation a vivo:Presentation .
        ?presentation rdfs:label ?presentationLabel 
    } UNION {
        ?subject ?property ?role .
        ?role a vivo:PresenterRole .
        ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation .
        ?presentation a vivo:Presentation .
        ?presentation vitro:mostSpecificType ?subclass .
    }
}

PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
CONSTRUCT { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference a bibo:Conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?conference rdfs:label ?conferenceLabel } WHERE {
{ ?subject ?property ?role . ?role a vivo:PresenterRole . } UNION { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?conference . ?conference <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?conference a bibo:Conference . ?conference rdfs:label ?conferenceLabel } }

PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
CONSTRUCT { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?series a vivo:EventSeries . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?series rdfs:label ?seriesLabel } WHERE {
{ ?subject ?property ?role . ?role a vivo:PresenterRole . } UNION { ?subject ?property ?role . ?role a vivo:PresenterRole . ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?presentation . ?presentation a vivo:Presentation . ?series a vivo:EventSeries . ?presentation <http://purl.obolibrary.org/obo/BFO_0000050> ?series . ?series <http://purl.obolibrary.org/obo/BFO_0000051> ?presentation . ?series rdfs:label ?seriesLabel } }
