list-view-config.query-select:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?role ?subclassLabel ?activity (REPLACE(STR(?activity),"^.*(#)(.*)$", "$2") AS ?activityLocal) ?activityName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?role . ?role a core:ReviewerRole .
OPTIONAL { ?subject ?property ?role . ?role a core:ReviewerRole . ?role core:roleContributesTo ?activity . ?activity rdfs:label ?activityName . } OPTIONAL { ?subject ?property ?role . ?role a core:ReviewerRole . ?role core:roleContributesTo ?activity . ?activity vitro:mostSpecificType ?subclass . ?subclass rdfs:label ?subclassLabel . } OPTIONAL { ?subject ?property ?role . ?role a core:ReviewerRole . ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?subject ?property ?role . ?role a core:ReviewerRole . ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
} ORDER BY ?subclass DESC(?dateTimeEnd) DESC(?dateTimeStart) ?activityName
