list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX fn: <http://www.w3.org/2005/xpath-functions#>
SELECT DISTINCT ?subclass ?editorship ?person ?personName WHERE { ?subject ?property ?editorship . ?editorship a core:Editorship . OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:rank ?rank . } OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . }
OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?person . ?person a foaf:Person . ?person vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf foaf:Person . }
FILTER ( bound(?person) )
} ORDER BY ?subclass ?rank (fn:lower-case(?personName))
