list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT DISTINCT ?subclass ?position ?positionTitle ?hrJobTitle ?rank ?org ?orgName ?middleOrg ?middleOrgName ?outerOrg ?outerOrgName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?position . ?position a core:Position . OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . ?org obo:BFO_0000050 ?middleOrg . ?middleOrg rdfs:label ?middleOrgName . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . ?org obo:BFO_0000050 ?middleOrg . ?middleOrg obo:BFO_0000050 ?outerOrg . ?outerOrg rdfs:label ?outerOrgName . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position rdfs:label ?positionTitle . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:hrJobTitle ?hrJobTitle . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:rank ?rank . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
OPTIONAL { ?subject ?property ?position . ?position a core:Position . ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:Position . }
FILTER ( bound(?org) )
} ORDER BY ?subclass DESC(?dateTimeEnd) ?rank
