list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT DISTINCT ?subclass ?vSubclass # send the property to the template, since this view supports multiple role properties ?property ?role ?roleLabel ?roleTypeLabel ?indivInRole (REPLACE(STR(?indivInRole),"^.*(#)(.*)$", "$2") AS ?indivName) ?indivLabel ?dateTimeInterval ?dateTimeStart ?dateTimeEnd ?objectType WHERE {
?subject ?property ?role . ?role a ?objectType .
OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role rdfs:label ?roleLabel . }
# We need ?subclass in the uncollated query to get the roleTypeLabel # for roles that have no label. OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role vitro:mostSpecificType ?subclass . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role vitro:mostSpecificType ?subclass . ?subclass rdfs:label ?roleTypeLabel . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . ?indivInRole rdfs:label ?indivLabel . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . ?indivInRole a vcard:Kind . ?indivInRole vcard:hasName ?vName . ?vName vcard:familyName ?lastName . OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . ?indivInRole a vcard:Kind . ?indivInRole vcard:hasName ?vName . ?vName vcard:givenName ?firstName . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . ?indivInRole a vcard:Kind . ?indivInRole vcard:hasName ?vName . ?vName core:middleName ?middleName . } bind ( COALESCE(?firstName, "") As ?firstName1) . bind ( COALESCE(?middleName, "") As ?middleName1) . bind ( COALESCE(?lastName, "") As ?lastName1) . bind (concat(str(?lastName1 + ", "),str(?firstName1 + " "),str(?middleName1)) as ?indivLabel) .
OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role <http://purl.obolibrary.org/obo/RO_0000052> ?indivInRole . ?indivInRole a vcard:Kind . ?indivInRole vitro:mostSpecificType ?vSubclass . ?vSubclass rdfs:subClassOf vcard:Kind . } } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?subject ?property ?role . ?role a ?objectType . ?role core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
FILTER ( bound(?indivInRole) )
} ORDER BY ?subclass ?indivLabel ?roleLabel ?roleTypeLabel ?indivName
