list-view-config.query-select:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT ?vcard ?link (REPLACE(STR(?link),"^.*(#)(.*)$", "$2") AS ?linkName) (group_concat(distinct ?linkLabel;separator="/") as ?label) ?url ?rank WHERE { ?subject ?property ?vcard . ?vcard vcard:hasURL ?link . OPTIONAL { ?subject ?property ?vcard . ?vcard vcard:hasURL ?link . ?link rdfs:label ?linkLabel . } OPTIONAL { ?subject ?property ?vcard . ?vcard vcard:hasURL ?link . ?link vcard:url ?url . } OPTIONAL { ?subject ?property ?vcard . ?vcard vcard:hasURL ?link . ?link core:rank ?rank . }
FILTER ( bound(?link) )
} GROUP BY ?vcard ?link ?url ?rank ORDER BY ?rank ?linkLabel
